{
  "query": "query GetStores($first: Int, $filters: StoreFilters) {
    stores(first: $first, filters: $filters) {
      totalCount
      pageInfo {
        hasNextPage
        hasPreviousPage
        startCursor
        endCursor
      }
      edges {
        cursor
        node {
          id
          code
          name
          logoURL
          websiteURL
          flyerSourceURL
          isActive
          lastScrapedAt
          scrapeSchedule
          createdAt
          updatedAt
          locations {
            city
            lat
            lng
            address
          }
        }
      }
    }
  }",
  "variables": {
    "first": 10,
    "filters": {
      "isActive": true
    }
  }
}

{
  "query": "query GetStoreByCode($code: String!) {
    storeByCode(code: $code) {
      id
      code
      name
      logoURL
      websiteURL
      flyerSourceURL
      isActive
      lastScrapedAt
      scrapeSchedule
      locations {
        city
        lat
        lng
        address
      }
      flyers(first: 5, filters: { isCurrent: true }) {
        totalCount
        edges {
          node {
            id
            title
            validFrom
            validTo
            isValid
            isCurrent
            daysRemaining
            pageCount
            productsExtracted
            status
          }
        }
      }
    }
  }",
  "variables": {
    "code": "iki"
  }
}

{
  "query": "query GetCurrentFlyers($storeIDs: [Int!], $first: Int) {
    currentFlyers(storeIDs: $storeIDs, first: $first) {
      totalCount
      pageInfo {
        hasNextPage
        hasPreviousPage
        startCursor
        endCursor
      }
      edges {
        cursor
        node {
          id
          title
          validFrom
          validTo
          isValid
          isCurrent
          daysRemaining
          validityPeriod
          pageCount
          productsExtracted
          status
          extractionStartedAt
          extractionCompletedAt
          processingDuration
          store {
            id
            code
            name
            logoURL
          }
          flyerPages(first: 3) {
            totalCount
            edges {
              node {
                id
                pageNumber
                imageURL
                imageWidth
                imageHeight
                hasImage
                productsExtracted
                status
              }
            }
          }
        }
      }
    }
  }",
  "variables": {
    "first": 20
  }
}

{
  "query": "query GetFlyerDetails($id: Int!) {
    flyer(id: $id) {
      id
      title
      validFrom
      validTo
      isValid
      isCurrent
      daysRemaining
      validityPeriod
      pageCount
      sourceURL
      productsExtracted
      status
      extractionStartedAt
      extractionCompletedAt
      processingDuration
      store {
        id
        code
        name
        logoURL
        websiteURL
      }
      flyerPages(first: 50) {
        totalCount
        edges {
          node {
            id
            pageNumber
            imageURL
            imageWidth
            imageHeight
            hasImage
            imageDimensions {
              width
              height
            }
            productsExtracted
            extractionEfficiency
            status
            extractionStartedAt
            extractionCompletedAt
            processingDuration
          }
        }
      }
      products(first: 10) {
        totalCount
        edges {
          node {
            id
            name
            brand
            category
            currentPrice
            originalPrice
            discountPercent
            discountAmount
            currency
            isOnSale
            isCurrentlyOnSale
          }
        }
      }
    }
  }",
  "variables": {
    "id": 1
  }
}

{
  "query": "query SearchProducts($query: String!, $filters: ProductFilters, $first: Int) {
    searchProducts(query: $query, filters: $filters, first: $first) {
      totalCount
      pageInfo {
        hasNextPage
        hasPreviousPage
        startCursor
        endCursor
      }
      edges {
        cursor
        node {
          id
          name
          normalizedName
          brand
          category
          subcategory
          description
          currentPrice
          originalPrice
          discountPercent
          discountAmount
          currency
          unitSize
          unitType
          unitPrice
          packageSize
          weight
          volume
          imageURL
          isOnSale
          isCurrentlyOnSale
          isAvailable
          validFrom
          validTo
          isValid
          extractionConfidence
          store {
            id
            code
            name
