{
  "query": "query GetMyShoppingLists($filters: ShoppingListFilters, $first: Int) {
    shoppingLists(filters: $filters, first: $first) {
      totalCount
      pageInfo {
        hasNextPage
        hasPreviousPage
        startCursor
        endCursor
      }
      edges {
        cursor
        node {
          id
          name
          description
          isDefault
          isArchived
          isPublic
          shareCode
          itemCount
          completedItemCount
          estimatedTotalPrice
          completionPercentage
          isCompleted
          canBeShared
          createdAt
          updatedAt
          lastAccessedAt
        }
      }
    }
  }",
  "variables": {
    "first": 20,
    "filters": {
      "isArchived": false
    }
  }
}

{
  "query": "mutation CreateShoppingList($input: CreateShoppingListInput!) {
    createShoppingList(input: $input) {
      id
      name
      description
      isDefault
      itemCount
      completedItemCount
      estimatedTotalPrice
      createdAt
