    checkShoppingListItem(id: $id) {
      id
      description
      isChecked
      checkedAt
      totalActualPrice
    }
  }",
  "variables": {
    "id": 1
  }
}

{
  "query": "query SuggestItems($query: String!, $limit: Int) {
    suggestItems(query: $query, limit: $limit) {
      description
      source
      estimatedPrice
      confidence
      productMaster {
        id
        canonicalName
        brand
        category
        standardUnitSize
      }
      product {
        id
        name
        brand
        currentPrice
        imageURL
        store {
          name
          code
        }
      }
    }
  }",
  "variables": {
    "query": "duona",
    "limit": 10
  }
}

{
  "query": "mutation AutoMatchItem($itemID: Int!) {
    autoMatchItem(itemID: $itemID) {
      itemID
      success
      reason
      item {
        id
        description
        isLinked
        matchingConfidence
      }
      match {
        product {
          id
          name
          brand
          currentPrice
          store {
            name
            code
          }
        }
        confidence
        priceMatch
        brandMatch
        sizeMatch
      }
    }
  }",
  "variables": {
    "itemID": 1
  }
}

{
  "query": "query AdvancedSearch($input: AdvancedSearchInput!) {
    advancedSearchProducts(input: $input) {
      totalCount
      queryTime
      hasMore
      suggestions
      products {
        searchScore
        matchType
        similarity
        highlights
        product {
          id
          name
          normalizedName
          brand
          category
          currentPrice
          originalPrice
          discountPercent
          currency
          unitSize
          unitPrice
          imageURL
          isOnSale
          isAvailable
          store {
            id
            code
            name
            logoURL
          }
        }
      }
    }
  }",
  "variables": {
    "input": {
      "query": "ekologiskas pienas",
      "minPrice": 1.0,
      "maxPrice": 5.0,
      "onSaleOnly": false,
      "category": "Pieno produktai",
      "limit": 20,
      "offset": 0,
      "preferFuzzy": true
    }
  }
}

{
  "query": "query FuzzySearch($input: FuzzySearchInput!) {
    fuzzySearchProducts(input: $input) {
      totalCount
      queryTime
      hasMore
      products {
        searchScore
        nameSimilarity
        brandSimilarity
        combinedSimilarity
        product {
          id
          name
          normalizedName
          brand
          category
          currentPrice
          originalPrice
          discountPercent
          currency
          unitSize
          imageURL
          isOnSale
          store {
            id
            code
            name
          }
        }
      }
    }
  }",
  "variables": {
    "input": {
      "query": "piens",
      "similarityThreshold": 0.5,
      "limit": 15,
      "offset": 0
    }
  }
}

{
  "query": "query HybridSearch($input: HybridSearchInput!) {
    hybridSearchProducts(input: $input) {
      totalCount
