      "offset": 0
    }
  }
}

{
  "query": "query SearchSuggestions($input: SearchSuggestionsInput!) {
    searchSuggestions(input: $input) {
      suggestions {
        text
        frequency
        minPrice
        maxPrice
        storeCount
      }
    }
  }",
  "variables": {
    "input": {
      "partialQuery": "pie",
      "limit": 10
    }
  }
}

{
  "query": "query SimilarProducts($input: SimilarProductsInput!) {
    similarProducts(input: $input) {
      products {
        similarityScore
        product {
          id
          name
          brand
          category
          currentPrice
          originalPrice
          discountPercent
          currency
          unitSize
          imageURL
          isOnSale
          store {
            id
            code
            name
          }
        }
      }
    }
  }",
  "variables": {
    "input": {
      "productID": 123,
      "limit": 10
    }
  }
}

{
  "query": "query SearchHealth {
    searchHealth {
      indexStatus {
        indexName
        isHealthy
        isUsed
      }
      lastRefreshTime
      suggestionCount
      averageQueryTime
      totalSearches
      errorRate
    }
  }"
}

{
  "query": "query GetPriceHistory($productID: Int!, $storeID: Int, $filters: PriceHistoryFilters, $first: Int) {
    priceHistory(productID: $productID, storeID: $storeID, filters: $filters, first: $first) {
      totalCount
      pageInfo {
        hasNextPage
        hasPreviousPage
        startCursor
        endCursor
      }
      edges {
        cursor
        node {
          id
          price
          originalPrice
          currency
          isOnSale
          recordedAt
          validFrom
          validTo
          saleStartDate
          saleEndDate
          source
          extractionMethod
          confidence
          isAvailable
          stockLevel
          isCurrentlyValid
          isCurrentlySale
          discountAmount
          discountPercent
          isExpired
          validityDuration
          product {
            id
            name
            brand
          }
          store {
            id
            code
            name
          }
        }
      }
    }
  }",
  "variables": {
    "productID": 123,
    "first": 30,
    "filters": {
      "startDate": "2025-10-01T00:00:00Z",
      "endDate": "2025-11-05T23:59:59Z"
    }
  }
}

{
  "query": "query GetCurrentPrice($productID: Int!, $storeID: Int!) {
    currentPrice(productID: $productID, storeID: $storeID) {
      id
      price
      originalPrice
      currency
      isOnSale
      recordedAt
      validFrom
      validTo
      saleStartDate
      saleEndDate
      isCurrentlyValid
      isCurrentlySale
      discountAmount
      discountPercent
      product {
        id
        name
        brand
        category
      }
      store {
        id
        code
        name
      }
    }
  }",
  "variables": {
    "productID": 123,
    "storeID": 1
  }
}

{
  "query": "query AnalyzeTrend($productID: Int!, $period: String!, $storeID: Int) {
    analyzeTrend(productID: $productID, period: $period, storeID: $storeID) {
      productID
      period
      direction
      trendPercentage
      confidence
      startPrice
      endPrice
      averageChange
      volatilityScore
      dataPoints
      trendStrength
      linearRegression {
        slope
        intercept
        rSquared
        pValue
        significant
      }
      movingAverages {
        ma7
        ma14
        ma30
        ma7AboveMa30
      }
    }
  }",
  "variables": {
    "productID": 123,
    "period": "30_days",
    "storeID": 1
  }
}

{
  "query": "query CompareStoreTrends($productID: Int!, $storeIDs: [Int!]!, $period: String!) {
    compareStoreTrends(productID: $productID, storeIDs: $storeIDs, period: $period) {
      productID
      divergence
      storeTrends {
        storeID
        storeName
        currentPrice
        priceStability
        trendAnalysis {
          direction
          trendPercentage
          confidence
          startPrice
          endPrice
          volatilityScore
          trendStrength
        }
      }
      bestStore {
        storeID
        storeName
        currentPrice
        priceStability
      }
      worstStore {
        storeID
        storeName
        currentPrice
        priceStability
      }
    }
  }",
  "variables": {
    "productID": 123,
    "storeIDs": [1, 2, 3],
    "period": "30_days"
  }
}

{
  "query": "query PredictPrice($productID: Int!, $daysAhead: Int!) {
    predictPrice(productID: $productID, daysAhead: $daysAhead) {
      productID
      predictionDate
      predictedPrice
      methodology
      accuracy
      confidenceInterval {
        lower
        upper
        confidence
      }
      factors {
        name
        impact
        description
      }
    }
  }",
  "variables": {
    "productID": 123,
    "daysAhead": 7
  }
}

{
  "query": "query GetBuyingRecommendation($productID: Int!) {
    buyingRecommendation(productID: $productID) {
      productID
      recommendation
      currentPrice
      recommendedAction
      confidence
      reasoning
      nextSaleDate
      potentialSavings
      waitDays
    }
  }",
  "variables": {
    "productID": 123
  }
}

{
  "query": "query GetSeasonalTrends($productID: Int!) {
    seasonalTrends(productID: $productID) {
      productID
      hasSeasonality
      seasonalIndex
      seasons {
        season
        startMonth
        endMonth
        avgPrice
        priceIndex
        nextStart
      }
      nextPeakSeason {
        season
        startMonth
        endMonth
        avgPrice
        priceIndex
        nextStart
      }
      nextLowSeason {
        season
        startMonth
        endMonth
        avgPrice
        priceIndex
        nextStart
      }
      recommendations {
        period
        advice
        potentialSavings
        timing
      }
    }
  }",
  "variables": {
    "productID": 123
  }
}

{
  "query": "mutation CreatePriceAlert($input: CreatePriceAlertInput!) {
    createPriceAlert(input: $input) {
      id
      alertType
      targetPrice
      dropPercent
      isActive
      notifyEmail
      notifyPush
      notes
      expiresAt
      createdAt
      product {
        id
        name
        brand
      }
      store {
        id
        code
        name
      }
