  "variables": {
    "input": {
      "productID": 123,
      "storeID": 1,
      "alertType": "TARGET_PRICE",
      "targetPrice": 1.20,
      "notifyEmail": true,
      "notifyPush": false,
      "notes": "Best price for this milk brand",
      "expiresAt": "2025-12-31T23:59:59Z"
    }
  }
}

{
  "query": "mutation RefreshSearchSuggestions {
    refreshSearchSuggestions {
      success
      refreshedAt
      suggestionCount
    }
  }"
}

