    productMasters(filters: $filters, first: $first) {
      totalCount
      pageInfo {
        hasNextPage
        hasPreviousPage
        startCursor
        endCursor
      }
      edges {
        cursor
        node {
          id
          canonicalName
          normalizedName
          brand
          category
          subcategory
          standardUnitSize
          standardUnitType
          standardPackageSize
          standardWeight
          standardVolume
          matchingKeywords
          alternativeNames
          exclusionKeywords
          confidenceScore
          matchedProducts
          successfulMatches
          failedMatches
          matchSuccessRate
          status
          isVerified
          lastMatchedAt
          verifiedAt
          verifiedBy
