{
  "query": "mutation RegisterUser($input: RegisterInput!) {
    register(input: $input) {
      user {
        id
        email
        fullName
        preferredLanguage
        isActive
        emailVerified
        createdAt
        updatedAt
      }
      accessToken
      refreshToken
      expiresAt
      tokenType
    }
  }",
  "variables": {
    "input": {
      "email": "newuser@example.com",
      "password": "SecurePassword123!",
      "fullName": "New User",
      "preferredLanguage": "lt"
    }
  }
}

{
  "query": "mutation LoginUser($input: LoginInput!) {
    login(input: $input) {
      user {
        id
        email
        fullName
        preferredLanguage
        isActive
        emailVerified
        lastLoginAt
      }
      accessToken
      refreshToken
      expiresAt
      tokenType
    }
  }",
  "variables": {
    "input": {
      "email": "newuser@example.com",
      "password": "SecurePassword123!"
    }
  }
}

{
  "query": "query GetCurrentUser {
    me {
      id
      email
      fullName
      preferredLanguage
      isActive
      emailVerified
      lastLoginAt
      createdAt
      updatedAt
    }
  }"
}

{
  "query": "query GetStores($first: Int, $filters: StoreFilters) {
    stores(first: $first, filters: $filters) {
      totalCount
      pageInfo {
        hasNextPage
        hasPreviousPage
        startCursor
        endCursor
      }
      edges {
        cursor
        node {
          id
          code
          name
          logoURL
          websiteURL
          flyerSourceURL
          isActive
          scraperConfig
          scrapeSchedule
          lastScrapedAt
          createdAt
          updatedAt
          locations {
            id
            city
            address
            latitude
            longitude
          }
        }
      }
    }
  }",
  "variables": {
    "first": 10,
    "filters": {
      "isActive": true
    }
  }
}

{
  "query": "query GetStore($id: Int!) {
    store(id: $id) {
      id
      code
      name
      logoURL
      websiteURL
      flyerSourceURL
      isActive
      locations {
        id
        city
        address
        postalCode
        latitude
        longitude
      }
      createdAt
      updatedAt
    }
  }",
  "variables": {
    "id": 1
  }
}

{
  "query": "query GetStoreByCode($code: String!) {
    storeByCode(code: $code) {
      id
      code
      name
      logoURL
      websiteURL
      isActive
      locations {
        city
        address
      }
      flyers(first: 5, filters: { isCurrent: true }) {
        totalCount
        edges {
          node {
            id
            title
            validFrom
            validTo
            isValid
            isCurrent
            daysRemaining
            pageCount
            productsExtracted
            status
          }
        }
      }
    }
  }",
  "variables": {
    "code": "iki"
  }
}

{
  "query": "query GetCurrentFlyers($storeIDs: [Int!], $first: Int) {
    currentFlyers(storeIDs: $storeIDs, first: $first) {
      totalCount
      pageInfo {
        hasNextPage
        endCursor
      }
      edges {
        cursor
        node {
          id
          title
          validFrom
          validTo
          isValid
          isCurrent
          daysRemaining
          validityPeriod
          pageCount
          productsExtracted
          status
          store {
            id
            code
            name
            logoURL
          }
        }
      }
    }
  }",
  "variables": {
    "first": 20
  }
}

{
  "query": "query GetFlyerDetails($id: Int!) {
    flyer(id: $id) {
      id
      title
      validFrom
      validTo
      isValid
      isCurrent
      daysRemaining
      validityPeriod
      pageCount
      sourceURL
      productsExtracted
      status
      store {
        id
        code
        name
        logoURL
        websiteURL
      }
      products(first: 20) {
        totalCount
        edges {
          node {
            id
            name
            brand
            category
            price {
              current
              original
              currency
              discountPercent
              discountAmount
              isDiscounted
            }
            isOnSale
            isCurrentlyOnSale
            imageURL
          }
        }
      }
    }
  }",
  "variables": {
    "id": 1
  }
}

{
  "query": "query GetProducts($first: Int, $filters: ProductFilters) {
    products(first: $first, filters: $filters) {
      totalCount
      pageInfo {
        hasNextPage
        endCursor
      }
      edges {
        cursor
        node {
          id
          name
          brand
          category
          subcategory
          price {
            current
            original
            currency
            discount
            discountPercent
            discountAmount
            isDiscounted
          }
          unitSize
          unitType
          unitPrice
          imageURL
          isOnSale
          isCurrentlyOnSale
          validFrom
          validTo
          isValid
          isExpired
          validityPeriod
          store {
            id
            code
            name
          }
          flyer {
            id
            title
            validFrom
            validTo
          }
        }
      }
    }
  }",
  "variables": {
    "first": 20,
    "filters": {
      "isOnSale": true
    }
  }
}

{
  "query": "query GetProductsOnSale($storeIDs: [Int!], $filters: ProductFilters, $first: Int) {
    productsOnSale(storeIDs: $storeIDs, filters: $filters, first: $first) {
      totalCount
      pageInfo {
        hasNextPage
        endCursor
      }
      edges {
        cursor
        node {
          id
          name
          brand
          category
          price {
            current
            original
            currency
            discountPercent
            discountAmount
          }
          unitSize
          unitPrice
          imageURL
          isOnSale
          isCurrentlyOnSale
          saleStartDate
          saleEndDate
          validFrom
          validTo
          store {
            id
            code
            name
            logoURL
          }
          flyer {
            id
            title
            validFrom
            validTo
            isCurrent
          }
        }
      }
    }
  }",
  "variables": {
    "first": 50,
    "filters": {
      "minPrice": 0.5,
      "maxPrice": 50.0,
      "categories": ["Pieno gaminiai"]
    }
  }
}

{
  "query": "query SearchProducts($input: SearchInput!) {
    searchProducts(input: $input) {
      queryString
      totalCount
      hasMore
      products {
        product {
          id
          name
          brand
          category
          price {
            current
            currency
          }
          imageURL
          isOnSale
          store {
            code
            name
          }
        }
        searchScore
        matchType
      }
    }
  }",
  "variables": {
    "input": {
      "q": "pienas",
      "first": 20
    }
  }
}

{
  "query": "query SearchProducts($input: SearchInput!) {
    searchProducts(input: $input) {
      queryString
