      "preferFuzzy": true
    }
  }
}

{
  "query": "query GetPriceHistory($productMasterID: Int!, $storeID: Int, $first: Int, $filters: PriceHistoryFilters) {
    priceHistory(productMasterID: $productMasterID, storeID: $storeID, first: $first, filters: $filters) {
      totalCount
      pageInfo {
        hasNextPage
        hasPreviousPage
        startCursor
        endCursor
      }
      edges {
        cursor
        node {
          id
          price
          currency
          isOnSale
          recordedAt
          storeID
          productMasterID
        }
      }
    }
  }",
  "variables": {
    "productMasterID": 1,
    "storeID": 1,
    "first": 10,
    "filters": {
      "isOnSale": true
    }
  }
}

{
  "query": "query GetCurrentPrice($productMasterID: Int!, $storeID: Int) {
    currentPrice(productMasterID: $productMasterID, storeID: $storeID) {
      id
      price
      currency
      isOnSale
      recordedAt
      storeID
      productMasterID
    }
  }",
  "variables": {
    "productMasterID": 1,
    "storeID": 1
  }
}

{
  "query": "query GetPriceHistoryDateRange($productMasterID: Int!, $first: Int, $filters: PriceHistoryFilters) {
    priceHistory(productMasterID: $productMasterID, first: $first, filters: $filters) {
      totalCount
      edges {
        node {
          id
          price
          currency
          isOnSale
          recordedAt
          storeID
        }
      }
    }
  }",
  "variables": {
    "productMasterID": 1,
    "first": 50,
    "filters": {
      "startDate": "2025-10-01",
      "endDate": "2025-11-06"
    }
  }
}

{
  "query": "query GetShoppingLists($filters: ShoppingListFilters) {
    shoppingLists(filters: $filters) {
      edges {
        cursor
        node {
          id
          name
          description
          isDefault
          isArchived
          isPublic
          itemCount
          completedItemCount
          completionPercentage
          isCompleted
          canBeShared
          createdAt
          updatedAt
          user {
            id
            email
            fullName
          }
        }
      }
      pageInfo {
        hasNextPage
        hasPreviousPage
      }
    }
  }",
  "variables": {
    "filters": {
      "isArchived": false
    }
  }
}

{
  "query": "query GetShoppingList($id: Int!) {
    shoppingList(id: $id) {
      id
      name
      description
      isDefault
      isArchived
      isPublic
      itemCount
      completedItemCount
      completionPercentage
      isCompleted
      canBeShared
      createdAt
      updatedAt
      items(first: 100) {
        totalCount
        edges {
          node {
            id
            description
            normalizedDescription
            notes
            quantity
            unit
            unitType
            isChecked
            checkedAt
            sortOrder
            category
            tags
            createdAt
            updatedAt
          }
        }
      }
    }
  }",
  "variables": {
    "id": 1
  }
}

{
  "query": "query GetDefaultShoppingList {
    myDefaultShoppingList {
      id
      name
      description
      isDefault
      itemCount
      completedItemCount
      completionPercentage
      isCompleted
      items(first: 100) {
        totalCount
        edges {
          node {
            id
            description
            quantity
            unit
            isChecked
            sortOrder
            category
          }
        }
      }
    }
  }"
}

{
  "query": "mutation CreateShoppingList($input: CreateShoppingListInput!) {
    createShoppingList(input: $input) {
      id
      name
      description
      isDefault
      isArchived
      isPublic
      createdAt
      updatedAt
      user {
        id
        email
      }
    }
  }",
  "variables": {
    "input": {
      "name": "My Shopping List",
      "description": "Weekly groceries",
      "isDefault": false
    }
  }
}

{
  "query": "mutation UpdateShoppingList($id: Int!, $input: UpdateShoppingListInput!) {\n    updateShoppingList(id: $id, input: $input) {\n      id\n      name\n      description\n      isDefault\n      updatedAt\n    }\n  }",
  "variables": {
    "id": 1,
    "input": {
      "name": "Updated Shopping List",
      "description": "Modified description"
    }
  }
}

{
  "query": "mutation DeleteShoppingList($id: Int!) {\n    deleteShoppingList(id: $id)\n  }",
  "variables": {
    "id": 1
  }
}

{
  "query": "mutation SetDefaultShoppingList($id: Int!) {
    setDefaultShoppingList(id: $id) {
      id
      name
      isDefault
    }
  }",
  "variables": {
    "id": 2
  }
}

