query SearchWithTags($input: SearchInput!) {
    searchProducts(input: $input) {
      queryString
      totalCount
      hasMore
      products {
        product {
          id
          name
          brand
          category
          tags
          price {
            current
            original
            currency
            discountPercent
          }
          store {
            code
            name
          }
          isOnSale
        }
        searchScore
        matchType
      }
    }
  }

